catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    try {
        var policy_list = sessionStorage['snapshot_policy'];
        var list = JSON.parse(policy_list);
        for (var i = 0; i < list.length; i++) {
            var local_rule = sessionStorage[list[i] + '_snapshot_policy'];
            var a = JSON.parse(local_rule);
            g_form.setDisplay(list[i] + '_snapshot_local_rule', 'true');
            g_form.setReadOnly(list[i] + '_snapshot_local_rule', 'false');
            for (var j = 0; j < a.length; j++) {
                if (a[j].status.uuid + '' == newValue + '') {
                    var val = a[j].status.resources.app_protection_rule_list[0].local_snapshot_retention_policy.snapshot_expiry_policy.multiple + '';
					var local_rule_uuid = a[j].status.resources.app_protection_rule_list[0].uuid+'';
                }
            }
            if (val + '' == '0') {
                g_form.clearOptions(list[i] + '_snapshot_local_rule');
                g_form.addOption(list[i] + '_snapshot_local_rule', local_rule_uuid, 'Snapshots have no expiry');
            } else {
                g_form.clearOptions(list[i] + '_snapshot_local_rule');
                g_form.addOption(list[i] + '_snapshot_local_rule', local_rule_uuid, 'Snapshots expire in ' + val + ' days');
            }
        }
    } catch (e) {
        console.log('Error inside Local rule on change of policy ' + e.message);
    }
}
