catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var portal_check;
    var portalgr = new GlideAjax('x_nuta2_nutanix_ca.Nutanix_ClientServerScriptBridge');
    portalgr.addParam('sysparm_name', 'getPortalCompatibility');
    portalgr.getXMLAnswer(getPortalCheck);

    function getPortalCheck(response) {
        portal_check = response;
        var cat_sys_id = g_form.getUniqueValue();
        var profile = (newValue + '').trim();
        var envUUID = (g_form.getValue('default_environment') + '').trim();
        var ga = new GlideAjax('x_nuta2_nutanix_ca.Nutanix_ClientServerScriptBridge');
        ga.addParam('sysparm_name', 'fetchSnapshotPolicy');
        ga.addParam('sysparm_catsysid', cat_sys_id);
        ga.addParam('sysparm_profile', profile);
        ga.addParam('sysparm_envSysId', envUUID);
        ga.getXML(varResponse);

        function varResponse(response) {
            var answer = response.responseXML.documentElement.getAttribute('answer');
            var parsed = JSON.parse(answer);
            var Snapshot_name = parsed.name + '';
            g_form.setDisplay(Snapshot_name + '_snapshot_policy', 'true');
            g_form.setReadOnly(Snapshot_name + '_snapshot_policy', 'false');
            var policyList = [];
            var SnapList = [];
            SnapList.push(Snapshot_name);
            var SnapshotList = parsed.list.entities;
            for (var i = 0; i < SnapshotList.length; i++) {
                policyList.push(SnapshotList[i].status.name + '');
                g_form.addOption(Snapshot_name + '_snapshot_policy', SnapshotList[i].status.uuid + '', SnapshotList[i].status.name + '');
                g_form.setValue(Snapshot_name + '_snapshot_policy', SnapshotList[i].status.name + '');
            }
            sessionStorage[Snapshot_name + '_snapshot_policy'] = JSON.stringify(SnapshotList);
            sessionStorage['snapshot_policy'] = JSON.stringify(SnapList);
        }
    }
}
