var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();
var action_utils = new Nutanix_Action_Utils();

current.state = 3; //make Requested Item's state as 'Closed Complete'
current.stage = 'complete';

var app_uuid = workflow.scratchpad.app_uuid;
var runlog_uuid = workflow.scratchpad.request_id;

var bpStatusResp = utils.makeCalmApiCall('GET', '/runbooks/runlogs/' + runlog_uuid, '');

var parsedJson = JSON.parse(bpStatusResp.getBody());

var grRunlogs = new GlideRecord(utils.configuration.table.appRunlogs);
grRunlogs.initialize();
grRunlogs.name = parsedJson.status.action_reference.name;
// grRunlogs.name = action_name + '';
grRunlogs.uuid = parsedJson.metadata.uuid;
grRunlogs.description = bpStatusResp.getBody();
grRunlogs.app_uuid = parsedJson.status.action_reference.uuid;
grRunlogs.app_name = parsedJson.status.action_reference.name;
grRunlogs.is_runlog_archived = parsedJson.status.is_runlog_archived;
grRunlogs.is_critical = parsedJson.status.is_critical;
grRunlogs.state_reason = (parsedJson.status.reason_list).toString(); //stringify;
grRunlogs.state = parsedJson.status.state;
grRunlogs.type = parsedJson.status.type;
grRunlogs.kind = parsedJson.metadata.kind;
grRunlogs.last_update_time = utils.calmDateConvertor(parsedJson.metadata.last_update_time);
grRunlogs.creation_time = utils.calmDateConvertor(parsedJson.metadata.creation_time);
grRunlogs.insert();

