var utils = new Nutanix_Calm_Utils();
var logger = new Nutanix_Calm_Logger();

var runbookStatusResp = utils.makeCalmApiCall('GET', '/runbooks/runlogs/' + workflow.scratchpad.request_id, '');

if(runbookStatusResp.getStatusCode() == '200' || runbookStatusResp.getStatusCode() == '201') {	
	workflow.scratchpad.requestState = JSON.parse(runbookStatusResp.getBody()).status.state + '';
	workflow.scratchpad.runbookStatusResp = runbookStatusResp;
	
} else {	
	logger.error("[wf_workflow] Blueprint  Pending Launches failed by "+workflow.scratchpad.user+"', Reason: "+bpStatusResp.getBody());
}

