var utils = new Nutanix_Calm_Utils();

current.state = 2; // to make the RITM's sate as 'Work In Progress'
current.stage = 'provisioning';

var profile_sys_id, blueprint_uuid;
var marketplace_uuid;
var runbook_execute;
var args_variable_list;
var response;
var project_uuid;
var profile = new GlideRecord('x_nuta2_nutanix_ca_application_profile');
profile.addEncodedQuery('name=runbook');
profile.query();
if (profile.next())
	profile_sys_id = profile.sys_id;

var catalog_item = new GlideRecord('x_nuta2_nutanix_ca_nutanix_runtime_configuration');
catalog_item.addEncodedQuery('state!=inactive^item_name=' + current.cat_item);
catalog_item.query();
if (catalog_item.next()) {
	blueprint_uuid = catalog_item.blueprint.uuid + '';
}
project_uuid = current.variables.default_project +'';
var runbook_uuid = blueprint_uuid + '';
workflow.scratchpad.bp_uuid = blueprint_uuid + '';

response = utils.makeCalmApiCall('GET', '/runbooks/' + runbook_uuid, '');
if (response.getStatusCode() == '200' || response.getStatusCode() == '201') {
	var response_body = JSON.parse(response.getBody());

	args_variable_list = response_body.spec.resources.runbook.variable_list;
	var request_body_arr = [];
	var mpi_json={};
	for (var i in args_variable_list) {
		var temp_json = args_variable_list[i];
		temp_json.value = current.variables[temp_json.name + '_' + profile_sys_id].getDisplayValue();
		request_body_arr.push(temp_json);

	}

	var request_body = {
		"spec": {
			"args": request_body_arr,
		}
	};

	runbook_execute = utils.makeCalmApiCall('POST', '/runbooks/' + runbook_uuid + '/run', JSON.stringify(request_body));
	workflow.scratchpad.bpLaunchedResp = runbook_execute;
	var request_body = JSON.parse(runbook_execute.getBody());

} else {
	logger.error("[wf_workflow] runbook run  operation failed " + JSON.stringify(response.getBody()));
}

